$(document).ready(function(){

	var original = document.getElementById("original"); 
	var prototype = document.getElementById("prototype"); 
	var final = document.getElementById("final"); 

	var swiperInstances = {};
	$(".swiper-container").each(function(index, element){

	    var $this = $(this);
	    $this.addClass("instance-" + index);

	    $this.closest('.carousel-wrapper').find(".paginate-left").addClass("nav-prev-" + index);
	    $this.closest('.carousel-wrapper').find(".paginate-right").addClass("nav-next-" + index);

	    swiperInstances[index] = new Swiper(".instance-" + index, {
	        direction: 'horizontal',
		    loop: true,
	        nextButton: ".nav-next-" + index,
	        prevButton: ".nav-prev-" + index
	    });

	});

	swiperInstances[0].on('slideChangeStart', function () {    
		$('.tag').removeClass('active');
	});

	swiperInstances[0].on('slideChangeEnd', function () {
		$('.tag').addClass('active');
	});

	$('video').on('play', function (e) {
		$('.tag').addClass('active');
	});

	//when scrolling...
	$(window).scroll(function() {

	    //if I scroll more than 1000px...
	    if($(window).scrollTop() > 330){
	    	$('.colourStrip').addClass('active');
	    } else {
	    	$('.colourStrip').removeClass('active');
	    }

	});

});